#include <iostream>\n#include <fstream>\n#include <string>\n\nusing namespace std;\n\nint main() {\n    string filename;\n    cout << "Please make sure the document is in the same directory as the program." << endl;\n    cout << "Please input document name (e.g., document01.cry): ";\n    cin >> filename;\n\n    ifstream infile(filename);\n\n    if (!infile) {\n        cerr << "File could not be opened!" << endl;\n        return 1;\n    }\n\n    char c;\n    bool skip = false;\n    while (infile.get(c)) {\n        if (!skip) {\n            cout.put(c);\n        }\n        skip = !skip;\n    }\n\n    infile.close();\n    getchar(); // To keep the console window open\n    return 0;\n}