#include <gtest/gtest.h>\n   #include <gmock/gmock.h>\n\n   using ::testing::Return;\n\n   TEST(FileLoaderTest, LoadFileToMem_Success) {\n       MockFileSystem mockFileSystem;\n       FileLoader fileLoader(mockFileSystem);\n\n       EXPECT_CALL(mockFileSystem, openfile(::testing::_))\n           .WillOnce(Return(successfull));\n       EXPECT_CALL(mockFileSystem, get_file_size())\n           .WillOnce(Return(successfull));\n       EXPECT_CALL(mockFileSystem, read_entire_file_to_buffer())\n           .WillOnce(Return(successfull));\n\n       EXPECT_TRUE(fileLoader.LoadFileToMem("testfile.txt"));\n   }\n\n   TEST(FileLoaderTest, LoadFileToMem_FailureInOpenFile) {\n       MockFileSystem mockFileSystem;\n       FileLoader fileLoader(mockFileSystem);\n\n       EXPECT_CALL(mockFileSystem, openfile(::testing::_))\n           .WillOnce(Return(failure));\n\n       EXPECT_FALSE(fileLoader.LoadFileToMem("testfile.txt"));\n   }\n\n   // Additional tests for other failure points can be added similarly.