#include <boost/asio.hpp>\n#include <iostream>\n\nint main() {\n    using namespace boost::asio;\n    io_service ios;\n    ip::tcp::iostream stream("localhost", "daytime");\n\n    if (!stream) {\n        std::cerr << "Unable to connect: " << stream.error().message() << std::endl;\n        return 1;\n    }\n\n    std::string file_content = "File content to send...";\n    stream << file_content;\n    return 0;\n}