#include <windows.h>\n#include <iostream>\n\ntypedef void (*ThirdPartyFunctionType)();\n\nint main() {\n    HINSTANCE hDll = LoadLibrary("thirdpartylibrary.dll");\n    if (hDll == NULL) {\n        std::cerr << "Failed to load DLL" << std::endl;\n        return 1;\n    }\n\n    ThirdPartyFunctionType ThirdPartyFunction = (ThirdPartyFunctionType)GetProcAddress(hDll, "ThirdPartyFunction");\n    if (ThirdPartyFunction == NULL) {\n        std::cerr << "Failed to get function address" << std::endl;\n        FreeLibrary(hDll);\n        return 1;\n    }\n\n    // Use the function\n    ThirdPartyFunction();\n\n    // Free the DLL\n    FreeLibrary(hDll);\n\n    return 0;\n}