#include "rxregsvc.h"\n     #include "aced.h"\n\n     void initApp()\n     {\n         // Initialization code for ObjectARX\n         acedRegCmds->addCommand(_T("MY_COMMANDS"), _T("MyCommand"), _T("MYCOMMAND"), ACRX_CMD_MODAL, myCommand);\n     }\n\n     void unloadApp()\n     {\n         // Cleanup code for ObjectARX\n         acedRegCmds->removeGroup(_T("MY_COMMANDS"));\n     }\n\n     extern "C" AcRx::AppRetCode acrxEntryPoint(AcRx::AppMsgCode msg, void* pkt)\n     {\n         switch (msg)\n         {\n         case AcRx::kInitAppMsg:\n             acrxUnlockApplication(pkt);\n             acrxRegisterAppMDIAware(pkt);\n             initApp();\n             break;\n         case AcRx::kUnloadAppMsg:\n             unloadApp();\n             break;\n         }\n         return AcRx::kRetOK;\n     }