#include <map>\n#include <vector>\n#include <iostream>\n\n#define foreach(var, container) \\n  for(auto var = (container).begin(); var != (container).end(); ++var)\n\n#define foreach_value(val, container) \\n  for(auto __it_##__LINE__ = (container).begin(); \\n      __it_##__LINE__ != (container).end(); ++__it_##__LINE__) \\n    for(auto &val = __it_##__LINE__->second; __it_##__LINE__ != (container).end(); __it_##__LINE__ = (container).end())\n\nint main() {\n    std::map<int, std::string> mymap = {{1, "one"}, {2, "two"}, {3, "three"}};\n\n    foreach_value(value, mymap) {\n        std::cout << value << std::endl;\n    }\n\n    return 0;\n}