#include <iostream>\n#include <Windows.h>\n#include <comdef.h>\n\n// GUID for IMyCSharpInterface\nconst CLSID CLSID_MyCSharpClass = {0xYYYYYYYY, 0xYYYY, 0xYYYY, {0xYY, 0xYY, 0xYY, 0xYY, 0xYY, 0xYY, 0xYY, 0xYY}};\nconst IID IID_IMyCSharpInterface = {0xXXXXXXXX, 0xXXXX, 0xXXXX, {0xXX, 0xXX, 0xXX, 0xXX, 0xXX, 0xXX, 0xXX, 0xXX}};\n\nint main()\n{\n    CoInitialize(NULL);\n\n    IUnknown* pUnknown = nullptr;\n    HRESULT hr = CoCreateInstance(CLSID_MyCSharpClass, NULL, CLSCTX_INPROC_SERVER, IID_IUnknown, (void**)&pUnknown);\n    if (SUCCEEDED(hr))\n    {\n        IDispatch* pDispatch = nullptr;\n        hr = pUnknown->QueryInterface(IID_IMyCSharpInterface, (void**)&pDispatch);\n        if (SUCCEEDED(hr))\n        {\n            DISPID dispid;\n            OLECHAR* methodName = L"MyMethod";\n            hr = pDispatch->GetIDsOfNames(IID_NULL, &methodName, 1, LOCALE_USER_DEFAULT, &dispid);\n            if (SUCCEEDED(hr))\n            {\n                DISPPARAMS params = { nullptr, nullptr, 0, 0 };\n                hr = pDispatch->Invoke(dispid, IID_NULL, LOCALE_USER_DEFAULT, DISPATCH_METHOD, &params, nullptr, nullptr, nullptr);\n            }\n            pDispatch->Release();\n        }\n        pUnknown->Release();\n    }\n\n    CoUninitialize();\n    return 0;\n}