#include <windows.h>\n#include <comdef.h>\n#include <iostream>\n#include <oleauto.h>\n#import "C:\\Program Files\\Common Files\\Microsoft Shared\\OFFICE14\\MSO.DLL" \\n    rename("DocumentProperties", "DocumentPropertiesXL") \\n    rename("RGB", "RBGXL")\n#import "C:\\Program Files\\Microsoft Office\\Office14\\EXCEL.EXE" \\n    rename("DialogBox", "DialogBoxXL") \\n    rename("RGB", "RGBXL") \\n    rename("CopyFile", "CopyFileXL")\n\nint main()\n{\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to initialize COM library." << std::endl;\n        return 1;\n    }\n\n    try {\n        Excel::_ApplicationPtr pXL;\n        hr = pXL.CreateInstance(L"Excel.Application");\n        if (FAILED(hr)) {\n            std::cerr << "Failed to create Excel application instance." << std::endl;\n            CoUninitialize();\n            return 1;\n        }\n\n        // Make Excel visible\n        pXL->Visible[0] = VARIANT_TRUE;\n\n        // Add a new workbook\n        Excel::_WorkbookPtr pWorkbook = pXL->Workbooks->Add();\n        Excel::_WorksheetPtr pWorksheet = pWorkbook->Worksheets->Item[1];\n\n        // Use the worksheet object as needed\n        pWorksheet->Cells->Item[1][1] = "Hello, Excel!";\n\n        // Save and close the workbook\n        pWorkbook->SaveAs(L"C:\\Path\\To\\Your\\ExcelFile.xlsx");\n        pWorkbook->Close(VARIANT_FALSE);\n        pXL->Quit();\n\n    } catch (_com_error &e) {\n        std::cerr << "COM error: " << e.ErrorMessage() << std::endl;\n    }\n\n    CoUninitialize();\n    return 0;\n}