#include <iostream>\n#include <fstream>\n#include <string>\n#include <cstdlib>\n\nusing namespace std;\n\nint main( int argc, char* argv[] )\n{\n    if(argc != 2)\n    {\n        cout << "Error syntax is incorrect!\nSyntax: " << argv[ 0 ] << " <file>\n";\n        return 0;\n    }\n\n    ifstream file(argv[ 1 ]);\n    if (!file.good()) {\n        cout << "File " << argv[1] << " does not exist.\n";\n        return 0;\n    }\n\n    string linha;\n    while(getline(file, linha))\n    {\n        size_t pos = linha.find("print \"");\n        if(pos != string::npos)\n        {\n            string text = linha.substr(pos + 7); // 7 is the length of 'print "'\n            if (!text.empty() && text.back() == '"') {\n                text.pop_back(); // Remove the trailing quote\n            }\n            cout << text << endl;\n        }\n    }\n\n    return 0;\n}