#include <windows.h>\n#include <comdef.h>\n#include "MyLibrary_h.h"\n\nint main()\n{\n    HRESULT hr = CoInitialize(NULL);\n    if (SUCCEEDED(hr))\n    {\n        IMyInterface* pMyInterface = nullptr;\n        hr = CoCreateInstance(CLSID_MyClass, NULL, CLSCTX_INPROC_SERVER, IID_IMyInterface, (void**)&pMyInterface);\n        if (SUCCEEDED(hr))\n        {\n            BSTR input = SysAllocString(L"Hello, World!");\n            BSTR output = NULL;\n            hr = pMyInterface->MyMethod(input, &output);\n            if (SUCCEEDED(hr))\n            {\n                wprintf(L"Output: %s\n", output);\n                SysFreeString(output);\n            }\n            SysFreeString(input);\n            pMyInterface->Release();\n        }\n        CoUninitialize();\n    }\n    return 0;\n}