#include <fstream>\n#include <iostream>\n#include <cstdlib>\n\nstd::ifstream convert("data.txt");\nchar var1[5];\nchar var2[5];\n\nconst int n = ([]() -> int {\n    convert.getline(var1, 2);\n    return std::atoi(var1);\n})();\n\nconst int m = ([]() -> int {\n    convert.getline(var2, 2);\n    return std::atoi(var2);\n})();\n\nint main() {\n    std::cout << "n: " << n << ", m: " << m << std::endl;\n    return 0;\n}