#using <System.dll>\n#using <HtmlAgilityPack.dll>\n\nusing namespace System;\nusing namespace HtmlAgilityPack;\n\nint main(array<System::String ^> ^args)\n{\n    HtmlDocument^ doc = gcnew HtmlDocument();\n    doc->LoadHtml("<html><body><p>This is a paragraph.</p></body></html>");\n\n    HtmlNodeCollection^ nodes = doc->DocumentNode->SelectNodes("//p");\n\n    for each (HtmlNode^ node in nodes)\n    {\n        Console::WriteLine(node->InnerText);\n    }\n\n    return 0;\n}