#include <iostream>\n#include <vector>\n#include <cmath>\n\nvoid findSubsets(const std::vector<int>& set) {\n    int n = set.size();\n    int subsetCount = pow(2, n); // Total number of subsets is 2^n\n\n    for (int i = 0; i < subsetCount; i++) {\n        std::vector<int> subset;\n        for (int j = 0; j < n; j++) {\n            // Check if jth element of set should be included in the subset\n            if (i & (1 << j)) {\n                subset.push_back(set[j]);\n            }\n        }\n\n        // Print the current subset\n        std::cout << "{ ";\n        for (int elem : subset) {\n            std::cout << elem << " ";\n        }\n        std::cout << "}\n";\n    }\n}\n\nint main() {\n    std::vector<int> set = {1, 2, 3, 4, 5};\n    findSubsets(set);\n    return 0;\n}