#include <iostream>\n#include <fstream>\n#include <cstdlib> // for exit()\nusing namespace std;\n\nstruct PersonId\n{\n    int fId;\n}; \n\nistream& operator >> (istream& is, PersonId &p)\n{\n    is >> p.fId;\n    return is;\n}\n\nint main ()\n{\n    ifstream indata("persons.txt", ios::in); // opens the file\n\n    if(!indata) \n    { // file couldn't be opened\n        cout << "Error: file could not be opened" << endl;\n        exit(1);\n    }\n\n    int n = 5;\n    PersonId* p = new PersonId[n];\n\n    for (int i = 0; i < n; ++i) \n    { \n        if (!(indata >> p[i])) \n        {\n            cout << "Error reading data" << endl;\n            break;\n        }\n        cout << "PersonId [" << i+1 << "] is " << p[i].fId << endl;\n    }\n\n    delete[] p;\n    return 0;\n}