#include <iostream>\n#include <comdef.h>\n#include <stdexcept>\n\nvoid CheckHRESULT(HRESULT hr) {\n    if (FAILED(hr)) {\n        throw _com_error(hr);\n    }\n}\n\nint main() {\n    try {\n        HRESULT hr = CoInitialize(NULL);\n        CheckHRESULT(hr);\n\n        // Other COM operations...\n\n        CoUninitialize();\n    } catch (const _com_error& e) {\n        std::wcerr << L"COM error: " << e.ErrorMessage() << std::endl;\n        return EXIT_FAILURE;\n    } catch (const std::exception& e) {\n        std::cerr << "Standard exception: " << e.what() << std::endl;\n        return EXIT_FAILURE;\n    } catch (...) {\n        std::cerr << "Unknown error occurred" << std::endl;\n        return EXIT_FAILURE;\n    }\n\n    return 0;\n}