#include <iostream>\n#include <comdef.h>\n\nvoid HandleCOMError(HRESULT hr, const char* msg) {\n    if (FAILED(hr)) {\n        _com_error err(hr);\n        std::wcerr << msg << L": " << err.ErrorMessage() << std::endl;\n        exit(EXIT_FAILURE);\n    }\n}\n\nint main() {\n    HRESULT hr = CoInitialize(NULL);\n    HandleCOMError(hr, "COM initialization failed");\n\n    // Other COM operations...\n\n    CoUninitialize();\n    return 0;\n}