#include <iostream>\n#include <comdef.h>\n\nvoid CheckHRESULT(HRESULT hr) {\n    if (FAILED(hr)) {\n        _com_error err(hr);\n        std::wcerr << L"Error: " << err.ErrorMessage() << std::endl;\n        exit(EXIT_FAILURE);\n    }\n}\n\nint main() {\n    HRESULT hr = CoInitialize(NULL);\n    CheckHRESULT(hr);\n\n    // Other COM operations...\n\n    CoUninitialize();\n    return 0;\n}