#include <windows.h>\n\nint main()\n{\n    SHELLEXECUTEINFO sei = { sizeof(sei) };\n    sei.lpVerb = TEXT("runas");\n    sei.lpFile = TEXT("PathToYourExecutable.exe");\n    sei.hwnd = NULL;\n    sei.nShow = SW_NORMAL;\n\n    if (!ShellExecuteEx(&sei))\n    {\n        DWORD dwError = GetLastError();\n        if (dwError == ERROR_CANCELLED)\n        {\n            // The user refused the elevation\n            MessageBox(NULL, TEXT("Elevation required to proceed."), TEXT("Error"), MB_OK);\n        }\n        else\n        {\n            // An error occurred\n            MessageBox(NULL, TEXT("Failed to elevate."), TEXT("Error"), MB_OK);\n        }\n    }\n    return 0;\n}