#include <iostream>\n#include <string>\n#include <map>\n\n// Function to replace accented characters in a string\nstd::string removeAccentsAndTildes(const std::string& input) {\n    std::map<char, char> dictionary = {\n        {'á', 'a'}, {'é', 'e'}, {'í', 'i'}, {'ó', 'o'}, {'ú', 'u'}, {'ñ', 'n'},\n        {'Á', 'A'}, {'É', 'E'}, {'Í', 'I'}, {'Ó', 'O'}, {'Ú', 'U'}, {'Ñ', 'N'}\n    };\n\n    std::string result = input;\n    for (size_t i = 0; i < result.size(); ++i) {\n        auto it = dictionary.find(result[i]);\n        if (it != dictionary.end()) {\n            result[i] = it->second;\n        }\n    }\n\n    return result;\n}\n\nint main() {\n    std::string toReplace = "ábéíóúñÁÉÍÓÚÑ";\n    std::string result = removeAccentsAndTildes(toReplace);\n    std::cout << result << std::endl; // Output should be "abeiounABEIOUN"\n    return 0;\n}