#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Open events created by Process A\n    HANDLE eventA = OpenEvent(EVENT_ALL_ACCESS, FALSE, TEXT("EventA"));\n    HANDLE eventB = OpenEvent(EVENT_ALL_ACCESS, FALSE, TEXT("EventB"));\n\n    if (eventA == NULL || eventB == NULL) {\n        std::cerr << "Failed to open events\n";\n        return 1;\n    }\n\n    while (true) {\n        // Wait for Process A to signal\n        WaitForSingleObject(eventB, INFINITE);\n\n        // Perform some fixed set of operations\n        std::cout << "Process B doing work\n";\n        Sleep(1000); // Simulate work\n\n        // Signal Process A to run\n        SetEvent(eventA);\n    }\n\n    CloseHandle(eventA);\n    CloseHandle(eventB);\n    return 0;\n}