#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Create events\n    HANDLE eventA = CreateEvent(NULL, FALSE, FALSE, TEXT("EventA"));\n    HANDLE eventB = CreateEvent(NULL, FALSE, FALSE, TEXT("EventB"));\n\n    // Launch Process B\n    STARTUPINFO si = { sizeof(si) };\n    PROCESS_INFORMATION pi;\n    if (!CreateProcess(TEXT("ProcessB.exe"), NULL, NULL, NULL, FALSE, 0, NULL, NULL, &si, &pi)) {\n        std::cerr << "Failed to start Process B\n";\n        return 1;\n    }\n\n    while (true) {\n        // Perform some fixed set of operations\n        std::cout << "Process A doing work\n";\n        Sleep(1000); // Simulate work\n\n        // Signal Process B to run\n        SetEvent(eventB);\n\n        // Wait for Process B to signal back\n        WaitForSingleObject(eventA, INFINITE);\n    }\n\n    CloseHandle(eventA);\n    CloseHandle(eventB);\n    CloseHandle(pi.hProcess);\n    CloseHandle(pi.hThread);\n    return 0;\n}