#include <iostream>\n\n// Example class\nclass MyClass {\npublic:\n    void memberFunction(int value) {\n        std::cout << "Value: " << value << std::endl;\n    }\n};\n\n// Define a type for a pointer to a member function\ntypedef void (MyClass::*MemberFunctionPointer)(int);\n\n// Structure to hold both the object pointer and the member function pointer\nstruct Callback {\n    MyClass* object;\n    MemberFunctionPointer function;\n\n    Callback(MyClass* obj, MemberFunctionPointer func) \n        : object(obj), function(func) {}\n\n    void call(int value) {\n        (object->*function)(value);\n    }\n};\n\nint main() {\n    MyClass myObject;\n    Callback callback(&myObject, &MyClass::memberFunction);\n    \n    // Invoke the member function through the callback\n    callback.call(42);\n\n    return 0;\n}