#include <iostream>\n#include <functional>\n\n// Example class\nclass MyClass {\npublic:\n    void memberFunction(int value) {\n        std::cout << "Value: " << value << std::endl;\n    }\n};\n\nint main() {\n    MyClass myObject;\n\n    // Using std::function and std::bind\n    std::function<void(int)> callback = std::bind(&MyClass::memberFunction, &myObject, std::placeholders::_1);\n\n    // Invoke the member function through the callback\n    callback(42);\n\n    return 0;\n}