#include <windows.h>\n#include <wininet.h>\n#include <stdio.h>\n\n#pragma comment(lib, "wininet.lib")\n\nint main() {\n    HINTERNET hSession = InternetOpen("MyAgent",\n        INTERNET_OPEN_TYPE_PRECONFIG, NULL, NULL, 0);\n    if (hSession == NULL) {\n        printf("InternetOpen failed\n");\n        return 1;\n    }\n\n    HINTERNET hConnect = InternetConnect(hSession, "example.com",\n        INTERNET_DEFAULT_HTTP_PORT, NULL, NULL, INTERNET_SERVICE_HTTP, 0, 1);\n    if (hConnect == NULL) {\n        printf("InternetConnect failed\n");\n        InternetCloseHandle(hSession);\n        return 1;\n    }\n\n    HINTERNET hRequest = HttpOpenRequest(hConnect, "POST", "/test.php", NULL, NULL, NULL, 0, 1);\n    if (hRequest == NULL) {\n        printf("HttpOpenRequest failed\n");\n        InternetCloseHandle(hConnect);\n        InternetCloseHandle(hSession);\n        return 1;\n    }\n\n    const char* boundary = "---------------------------1234567890";\n    char hdrs[256];\n    sprintf(hdrs, "Content-Type: multipart/form-data; boundary=%s", boundary);\n\n    const char* filename = "filename.txt";\n    char frmdata[1024];\n    sprintf(frmdata,\n        "--%s\r\n"\n        "Content-Disposition: form-data; name=\"file\"; filename=\"%s\"\r\n"\n        "Content-Type: text/plain\r\n\r\n"\n        "This is the content of the file.\r\n"\n        "--%s--\r\n",\n        boundary, filename, boundary);\n\n    BOOL bRequestSent = HttpSendRequest(hRequest, hdrs, strlen(hdrs), frmdata, strlen(frmdata));\n    if (!bRequestSent) {\n        printf("HttpSendRequest failed\n");\n    } else {\n        printf("HttpSendRequest succeeded\n");\n    }\n\n    InternetCloseHandle(hRequest);\n    InternetCloseHandle(hConnect);\n    InternetCloseHandle(hSession);\n\n    return 0;\n}