#include <windows.h>\n#include <tchar.h>\n#include <iostream>\n\nbool CheckDiskSpace()\n{\n    ULARGE_INTEGER freeBytesAvailableToCaller;\n    ULARGE_INTEGER totalNumberOfBytes;\n    ULARGE_INTEGER totalNumberOfFreeBytes;\n\n    if (GetDiskFreeSpaceEx(_T("\\"), &freeBytesAvailableToCaller, &totalNumberOfBytes, &totalNumberOfFreeBytes))\n    {\n        // Example threshold: 50 MB\n        const ULONGLONG threshold = 50ULL * 1024 * 1024;\n\n        if (freeBytesAvailableToCaller.QuadPart < threshold)\n        {\n            std::cout << "Warning: Low storage space!" << std::endl;\n            return false;\n        }\n        else\n        {\n            std::cout << "Storage space is sufficient." << std::endl;\n            return true;\n        }\n    }\n    else\n    {\n        std::cerr << "Failed to get disk space information." << std::endl;\n        return false;\n    }\n}\n\nint main()\n{\n    // Polling loop\n    while (true)\n    {\n        if (!CheckDiskSpace())\n        {\n            // Handle low storage situation\n            // For example, notify the user or clean up some space\n        }\n\n        // Wait for some time before checking again\n        Sleep(60000); // Check every 60 seconds\n    }\n\n    return 0;\n}