#include <mysql++.h>\n    #include <ssqls.h>\n\n    using namespace std;\n    using namespace mysqlpp;\n\n    int main() {\n        try {\n            Connection conn("database", "host", "user", "password");\n\n            Query query = conn.query("SELECT * FROM table");\n            StoreQueryResult res = query.store();\n\n            for (size_t i = 0; i < res.num_rows(); ++i) {\n                cout << "Row " << i << ": " << res[i]["column"] << endl;\n            }\n        }\n        catch (const BadQuery& e) {\n            cerr << "Query error: " << e.what() << endl;\n            return 1;\n        }\n        catch (const Exception& e) {\n            cerr << "Error: " << e.what() << endl;\n            return 1;\n        }\n\n        return 0;\n    }