#include <windows.h>\n\nvoid WriteToSharedMemory(int newValue) {\n    HANDLE hMapFile = OpenFileMapping(FILE_MAP_ALL_ACCESS, FALSE, L"Global\\MySharedMemory");\n    if (hMapFile == NULL) return;\n\n    int* pBuf = (int*)MapViewOfFile(hMapFile, FILE_MAP_ALL_ACCESS, 0, 0, sizeof(int));\n    if (pBuf == NULL) return;\n\n    *pBuf = newValue;\n\n    UnmapViewOfFile(pBuf);\n    CloseHandle(hMapFile);\n}