#include <iostream>\n#include <vector>\n#include <string>\n#include <sstream>\n#include <iterator>\n\n// Function to join vector elements with a delimiter\ntemplate<typename T>\nstd::string join(const std::vector<T>& elements, const std::string& delimiter) {\n    std::ostringstream os;\n    auto it = elements.begin();\n    if (it != elements.end()) {\n        os << *it++;\n        for (; it != elements.end(); ++it) {\n            os << delimiter << *it;\n        }\n    }\n    return os.str();\n}\n\nint main() {\n    std::vector<std::string> columns = {"column1", "column2", "column3"};\n\n    std::string selectSql = "SELECT " + join(columns, ", ") + " FROM some_table";\n\n    std::cout << selectSql << std::endl;  // Output: SELECT column1, column2, column3 FROM some_table\n\n    return 0;\n}