#include <windows.h>\n#include <mstask.h>\n#include <objbase.h>\n\n#pragma comment(lib, "mstask.lib")\n\nint main() {\n    HRESULT hr;\n    ITaskScheduler *pITS = NULL;\n\n    hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        // Handle the failure\n        return 1;\n    }\n\n    hr = CoCreateInstance(CLSID_CTaskScheduler,\n                          NULL,\n                          CLSCTX_INPROC_SERVER,\n                          IID_ITaskScheduler,\n                          (void **) &pITS);\n    if (SUCCEEDED(hr)) {\n        // Use the task scheduler\n        pITS->Release();\n    } else {\n        // Handle the failure\n    }\n\n    CoUninitialize();\n    return 0;\n}