#include <iostream>\n#include <memory>\n\nvoid processStream(std::unique_ptr<std::istream> streamPtr) {\n    std::string line;\n    while (std::getline(*streamPtr, line)) {\n        std::cout << line << std::endl;\n    }\n}\n\nint main() {\n    auto streamPtr = std::make_unique<std::ifstream>("example.txt");\n    if (streamPtr->is_open()) {\n        processStream(std::move(streamPtr));\n    } else {\n        std::cerr << "Failed to open file." << std::endl;\n    }\n    return 0;\n}