#include <windows.h>\n#include <iostream>\n\nvoid lock_file(HANDLE hFile) {\n    OVERLAPPED ov = {0};\n    if (!LockFileEx(hFile, LOCKFILE_EXCLUSIVE_LOCK, 0, MAXDWORD, MAXDWORD, &ov)) {\n        std::cerr << "LockFileEx failed: " << GetLastError() << std::endl;\n    }\n}\n\nvoid unlock_file(HANDLE hFile) {\n    OVERLAPPED ov = {0};\n    if (!UnlockFileEx(hFile, 0, MAXDWORD, MAXDWORD, &ov)) {\n        std::cerr << "UnlockFileEx failed: " << GetLastError() << std::endl;\n    }\n}\n\nint main() {\n    HANDLE hFile = CreateFile("C:\\path\\to\\your\\file.xml", GENERIC_READ | GENERIC_WRITE, 0, NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, NULL);\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::cerr << "CreateFile failed: " << GetLastError() << std::endl;\n        return 1;\n    }\n\n    lock_file(hFile);\n    // Perform file operations (read or write)\n    unlock_file(hFile);\n\n    CloseHandle(hFile);\n    return 0;\n}