#include <iostream>\n#include <fstream>\n\nint main(){\n    struct student\n    {\n        int rollNo;\n    };\n    struct student stud1;\n    stud1.rollNo = 1;\n\n    std::ofstream fout;\n    fout.open("stu1.dat", std::ios::binary);\n    fout.write((char*)&stud1, sizeof(stud1));\n    fout.close();\n\n    std::ifstream filin("stu1.dat", std::ios::binary);\n    struct student tmpStu;\n    while(filin.read((char*)&tmpStu, sizeof(tmpStu)))\n    {\n        std::cout << tmpStu.rollNo << std::endl; \n    }\n    filin.close();\n}