#include "Poco/Process.h"\n#include "Poco/Pipe.h"\n#include "Poco/StreamCopier.h"\n#include <iostream>\n\nint main() {\n    Poco::Pipe outPipe;\n    std::vector<std::string> args;\n    args.push_back("--version");\n\n    Poco::ProcessHandle ph = Poco::Process::launch("python", args, 0, &outPipe, 0);\n    Poco::PipeInputStream istr(outPipe);\n    Poco::StreamCopier::copyStream(istr, std::cout);\n\n    int rc = ph.wait();\n    std::cout << "Process exited with code: " << rc << std::endl;\n\n    return 0;\n}