#include <iostream>\n\nclass name\n{\npublic:\n    int a;\n    name() : a(0) {};\n};\n\nvoid add(name * pname)\n{\n    pname = NULL;\n}\n\nint main()\n{\n    name varName; // Correctly creates an instance of the 'name' class\n    name * pName = new name();\n    \n    add(pName);\n    add(&varName); // This will now work correctly\n    \n    // Clean up dynamically allocated memory\n    delete pName;\n\n    return 0;\n}