#include "interface.h"\n#include <iostream>\n#include <windows.h>\n\nint main() {\n    HMODULE dll;\n    IClass* (*CreateClass)();\n    IClass* dllclass;\n\n    std::cout << "Loading a.dll\n";\n    dll = LoadLibraryW(L"a.dll");\n    if (!dll) {\n        std::cerr << "Failed to load DLL\n";\n        return 1;\n    }\n\n    CreateClass = (IClass* (*)())GetProcAddress(dll, "CreateClass");\n    if (!CreateClass) {\n        std::cerr << "Failed to get CreateClass function\n";\n        FreeLibrary(dll);\n        return 1;\n    }\n\n    dllclass = CreateClass();\n    if (!dllclass) {\n        std::cerr << "CreateClass failed\n";\n        FreeLibrary(dll);\n        return 1;\n    }\n\n    dllclass->PrintSomething(dllclass);\n    dllclass->refCounted.Release((IRefCounted*)dllclass);\n    FreeLibrary(dll);\n\n    std::cout << "Done, press enter to exit." << std::endl;\n    std::cin.get();\n    return 0;\n}