#include <windows.h>\n#include <iostream>\n\nvoid SetProtectedOperatingSystemFile(const char* filePath, bool isProtected) {\n    DWORD attributes = GetFileAttributes(filePath);\n    if (attributes == INVALID_FILE_ATTRIBUTES) {\n        std::cerr << "Could not get file attributes. Error: " << GetLastError() << std::endl;\n        return;\n    }\n\n    if (isProtected) {\n        attributes |= FILE_ATTRIBUTE_SYSTEM;\n    } else {\n        attributes &= ~FILE_ATTRIBUTE_SYSTEM;\n    }\n\n    if (!SetFileAttributes(filePath, attributes)) {\n        std::cerr << "Could not set file attributes. Error: " << GetLastError() << std::endl;\n    } else {\n        std::cout << "File attributes set successfully." << std::endl;\n    }\n}\n\nint main() {\n    const char* filePath = "C:\\path\\to\\your\\file.txt";\n    bool isProtected = true; // Set to false to unmark as protected\n\n    SetProtectedOperatingSystemFile(filePath, isProtected);\n\n    return 0;\n}