#include <iostream>\n#include <cstdlib>\n\nvoid convertHtmlToPdf(const std::string& inputHtml, const std::string& outputPdf) {\n    std::string command = "wkhtmltopdf \"" + inputHtml + "\" \"" + outputPdf + "\"";\n    int result = system(command.c_str());\n    if (result != 0) {\n        std::cerr << "Error: Failed to convert HTML to PDF" << std::endl;\n    } else {\n        std::cout << "Conversion succeeded: " << outputPdf << std::endl;\n    }\n}\n\nint main() {\n    std::string inputHtml = "input.html";  // Path to your input HTML file\n    std::string outputPdf = "output.pdf";  // Path where you want to save the output PDF\n\n    convertHtmlToPdf(inputHtml, outputPdf);\n\n    return 0;\n}