#include <windows.h>\n#include <iostream>\n\nbool CopyLockedFile(LPCTSTR sourceFilePath, LPCTSTR destinationFilePath)\n{\n    return CopyFileEx(\n        sourceFilePath,\n        destinationFilePath,\n        NULL,\n        NULL,\n        FALSE,\n        COPY_FILE_COPY_SYMLINK\n    );\n}\n\nint main()\n{\n    if (CopyLockedFile("C:\\path\\to\\lockedfile.txt", "C:\\path\\to\\destination.txt"))\n    {\n        std::cout << "File copied successfully." << std::endl;\n    }\n    else\n    {\n        std::cout << "Failed to copy file. Error: " << GetLastError() << std::endl;\n    }\n    return 0;\n}