#include <memory>\n   #include <odb/database.hxx>\n   #include <odb/mysql/database.hxx>\n   #include <odb/transaction.hxx>\n   #include <my_class.hxx>\n   #include <my_class-odb.hxx>\n\n   int main() {\n       std::unique_ptr<odb::database> db(new odb::mysql::database("user", "password", "db"));\n       typedef odb::query<MyClass> query;\n       typedef odb::result<MyClass> result;\n\n       odb::transaction t(db->begin());\n       result r(db->query<MyClass>(query::name == "example_name"));\n\n       for (const MyClass& obj : r) {\n           std::cout << obj.name() << std::endl;\n       }\n       t.commit();\n   }