#include <iostream>\n#include <string>\n\nstd::string leftPad(const std::string &str, size_t width, char padChar = ' ') {\n    if (str.length() >= width) {\n        return str;\n    }\n    return std::string(width - str.length(), padChar) + str;\n}\n\nint main() {\n    std::string str = "123";\n    size_t width = 10;\n    std::string paddedStr = leftPad(str, width);\n\n    std::cout << "\"" << paddedStr << "\"" << std::endl;  // Output: "       123"\n\n    return 0;\n}