// Common interface header (shared by all DLLs)\nclass IInternalClass {\npublic:\n    virtual void method1() = 0;\n    virtual void method2() = 0;\n    virtual void newMethod() = 0;  // New method in the interface\n};\n\n// DLL 1 implementation\nclass InternalClass : public IInternalClass {\npublic:\n    void method1() override;\n    void method2() override;\n    void newMethod() override;\n};\n\nInternalClass m_class;\nIInternalClass* getInternalObject() {\n    return &m_class;\n}\n\n// DLL 2 usage\nIInternalClass* classPtr = getInternalObject();\nclassPtr->method1();\nclassPtr->newMethod();