#include <string>\n#include <iostream>\n#include <sstream>\nusing namespace std;\n\nconst string htmlLine(string s)\n{\n    s += "<br />";\n    return s;\n}\n\nint main(int argc, char *argv[])\n{\n    stringstream s;\n    s << argc;\n    cout << htmlLine("woot") << htmlLine(s.str());\n    for (int i = 0; i < argc; ++i)\n    {\n        s.str("");\n        s << i << " = " << argv[i];\n        cout << htmlLine(s.str());\n    }\n    return 0;\n}