#include <windows.h>\n    #include <iostream>\n    #include "base.h"\n\n    typedef base::Sprite* (*CreateSpriteInstanceFunc)();\n\n    int main() {\n        HMODULE hDll = LoadLibrary("yourdll.dll");\n        if (!hDll) {\n            std::cerr << "Failed to load DLL" << std::endl;\n            return -1;\n        }\n\n        CreateSpriteInstanceFunc createInstance = (CreateSpriteInstanceFunc)GetProcAddress(hDll, "CreateSpriteInstance");\n        if (!createInstance) {\n            std::cerr << "Failed to get CreateSpriteInstance function" << std::endl;\n            FreeLibrary(hDll);\n            return -1;\n        }\n\n        base::Sprite* sprite = createInstance();\n        if (sprite) {\n            // Use the sprite instance\n            sprite->Draw(0, 0);\n\n            // Release the sprite when done\n            sprite->Release();\n        }\n\n        FreeLibrary(hDll);\n        return 0;\n    }