// WrapperB.cpp\n#include <windows.h>\n\ntypedef void (*VendorLibUtilsFuncB)();\n\nHMODULE hModuleB = NULL;\nVendorLibUtilsFuncB pFuncB = NULL;\n\nextern "C" __declspec(dllexport) void InitVendorLibUtilsB() {\n    hModuleB = LoadLibrary("VendorLibUtilsB.dll");\n    if (hModuleB) {\n        pFuncB = (VendorLibUtilsFuncB)GetProcAddress(hModuleB, "VendorLibUtilsFunction");\n    }\n}\n\nextern "C" __declspec(dllexport) void CallVendorLibUtilsFunctionB() {\n    if (pFuncB) {\n        pFuncB();\n    }\n}