#include <range/v3/all.hpp>\n#include <vector>\n#include <iostream>\n\nint main() {\n    using std::vector;\n    using namespace ranges;\n\n    vector<int> numbers;\n    for (int i = -10; i <= 10; ++i) {\n        numbers.push_back(i);\n    }\n\n    auto positive_numbers = numbers | views::filter([](int num) { return num > 0; });\n\n    // Output the positive numbers\n    for (int num : positive_numbers) {\n        std::cout << num << " ";\n    }\n    std::cout << std::endl;\n\n    return 0;\n}