#include <windows.h>\n#include <iostream>\n\nvoid GetClusterSize(const char* drive) {\n    DWORD sectorsPerCluster;\n    DWORD bytesPerSector;\n    DWORD numberOfFreeClusters;\n    DWORD totalNumberOfClusters;\n\n    if (GetDiskFreeSpace(drive, &sectorsPerCluster, &bytesPerSector, &numberOfFreeClusters, &totalNumberOfClusters)) {\n        DWORD bytesPerCluster = sectorsPerCluster * bytesPerSector;\n        std::cout << "Bytes per cluster: " << bytesPerCluster << std::endl;\n    } else {\n        std::cerr << "Error getting disk free space: " << GetLastError() << std::endl;\n    }\n}\n\nint main() {\n    GetClusterSize("C:\\");\n    return 0;\n}