#include <vector>\n\nint HashVector(const std::vector<int>& v) {\n    const int N = 1000000007; // Large prime number for modulo\n    const int w[] = {234, 739, 934, 23, 828, 194}; // Carefully chosen constants\n    int result = 0;\n    int len = v.size() < 6 ? v.size() : 6; // Ensure we don't exceed the length of w\n    for (int i = 0; i < len; ++i) {\n        result = (result + w[i] * v[i]) % N;\n    }\n    return result;\n}