#include <Windows.h>\n#include <tchar.h>\n\n// Global Mutex handle\nHANDLE g_hMutex = NULL;\n\nbool IsAlreadyRunning()\n{\n    // Attempt to create a mutex with a unique name\n    g_hMutex = CreateMutex(NULL, TRUE, _T("Global\\MyUniqueMutexName"));\n\n    // Check if the mutex already exists\n    if (g_hMutex && GetLastError() == ERROR_ALREADY_EXISTS)\n    {\n        // Mutex already exists, another instance is running\n        CloseHandle(g_hMutex);\n        g_hMutex = NULL;\n        return true;\n    }\n    return false;\n}\n\nint APIENTRY _tWinMain(HINSTANCE hInstance,\n                       HINSTANCE hPrevInstance,\n                       LPTSTR    lpCmdLine,\n                       int       nCmdShow)\n{\n    if (IsAlreadyRunning())\n    {\n        // Another instance is already running, exit this one\n        return 0;\n    }\n\n    // Initialize the MFC application and create the window\n    // ...\n\n    // Message loop\n    // ...\n\n    // Cleanup\n    if (g_hMutex)\n    {\n        CloseHandle(g_hMutex);\n    }\n\n    return 0;\n}