#include <Windows.h>\n#include "MyClass.h"\n\ntypedef MyClass* (*CreateMyClassFunc)();\ntypedef void (*DestroyMyClassFunc)(MyClass*);\n\nint main() {\n    HMODULE hModule = LoadLibrary("MyDll.dll");\n    if (!hModule) {\n        // Handle the error\n        return -1;\n    }\n\n    CreateMyClassFunc createMyClass = (CreateMyClassFunc)GetProcAddress(hModule, "CreateMyClass");\n    DestroyMyClassFunc destroyMyClass = (DestroyMyClassFunc)GetProcAddress(hModule, "DestroyMyClass");\n\n    if (!createMyClass || !destroyMyClass) {\n        // Handle the error\n        FreeLibrary(hModule);\n        return -1;\n    }\n\n    MyClass* myClassInstance = createMyClass();\n    myClassInstance->myMethod();\n    destroyMyClass(myClassInstance);\n\n    FreeLibrary(hModule);\n    return 0;\n}