#include <windows.h>\n   #include <iostream>\n\n   int main()\n   {\n       CoInitialize(NULL);\n       CLSID clsid;\n       HRESULT hr = CLSIDFromProgID(OLESTR("MyComClass"), &clsid);\n       if (FAILED(hr))\n       {\n           std::cerr << "CLSIDFromProgID failed" << std::endl;\n           return 1;\n       }\n\n       IMyComInterface* pComObject = nullptr;\n       hr = CoCreateInstance(clsid, NULL, CLSCTX_LOCAL_SERVER, IID_PPV_ARGS(&pComObject));\n       if (FAILED(hr))\n       {\n           std::cerr << "CoCreateInstance failed" << std::endl;\n           return 1;\n       }\n\n       pComObject->MyMethod();\n       pComObject->Release();\n       CoUninitialize();\n       return 0;\n   }